import React, { useState } from 'react'; // 1. Importing the "Memory" tool
import './App.css'; 

function App() {
  // 2. Setting up the "Memory" (State)
  const [searchId, setSearchId] = useState(''); 
  const [dogData, setDogData] = useState(null);

  // 3. The "Action" (The Search Function)
  const handleSearch = () => {
    const mockDatabase = [
      { DogID: "RED108", TagNumber: "108", Status: "VACCINATED" },
      { DogID: "YELLOW220", TagNumber: "220", Status: "BOOSTER NEEDED" }
    ];

    const result = mockDatabase.find(dog => dog.DogID === searchId.toUpperCase());
    setDogData(result);
  };

  // 4. The "Face" (The HTML)
  return (
    <div className="App">
      <h1>üêï RabiesAware Registry</h1>
      <input 
        type="text" 
        placeholder="Search Tag ID..." 
        value={searchId}
        onChange={(e) => setSearchId(e.target.value)} 
      />
      <button onClick={handleSearch}>Search</button>

      {dogData && (
        <div className="card">
          <h2>Status: {dogData.Status}</h2>
          <p>Tag: {dogData.TagNumber}</p>
        </div>
      )}
    </div>
  );
}

export default App;
