import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Circle } from "react-leaflet";
import MarkerClusterGroup from "react-leaflet-markercluster";
import axios from "axios";
import "leaflet/dist/leaflet.css";
import "react-leaflet-markercluster/dist/styles.min.css";

const MapComponent = ({ backendUrl }) => {
  const [sightings, setSightings] = useState([]);
  const [newSighting, setNewSighting] = useState({ lat: null, lng: null });

  useEffect(() => {
    // Mock data for demo if backend isn't ready
    setSightings([
      { lat: 9.9312, lng: 76.2673 }, 
      { lat: 9.9320, lng: 76.2680 }
    ]);
  }, []);

  const useCurrentLocation = () => {
    if (!navigator.geolocation) return alert("Geolocation not supported");
    navigator.geolocation.getCurrentPosition(
      (position) => setNewSighting({ lat: position.coords.latitude, lng: position.coords.longitude }),
      () => alert("Unable to retrieve location")
    );
  };

  const defaultCenter = [9.9312, 76.2673]; // Kochi coordinates

  return (
    <div>
      <div style={{ marginBottom: "10px", display: 'flex', gap: '10px' }}>
        <button onClick={useCurrentLocation} className="btn-verify">üìç Use My Location</button>
        <button className="btn-verify" style={{background: '#ffbb33'}}>Report Sighting</button>
      </div>
      <MapContainer center={defaultCenter} zoom={13} style={{ height: "350px", width: "100%", borderRadius: '8px' }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        <MarkerClusterGroup>
          {sightings.map((s, idx) => (
            <Marker key={idx} position={[s.lat, s.lng]} />
          ))}
        </MarkerClusterGroup>
        {newSighting.lat && <Circle center={[newSighting.lat, newSighting.lng]} radius={100} color="red" />}
      </MapContainer>
    </div>
  );
};

export default MapComponent;
