import React, { useState } from 'react';
import './App.css';

function App() {
  // 1. State (Memory): Stores what the user types and the search results
  const [location, setLocation] = useState('');
  const [hospitalData, setHospitalData] = useState(null);

  // 2. Logic (Action): The search function
  const handleSearch = () => {
    // This "Mock Database" represents your backend data
    const mockHospitals = [
      { city: "KOCHI", name: "City Medical Center", distance: "0.8 km", phone: "+91 484 2345678" },
      { city: "KOCHI", name: "St. George Hospital", distance: "1.5 km", phone: "+91 484 8765432" },
      { city: "TRIVANDRUM", name: "General Health Clinic", distance: "2.1 km", phone: "+91 471 1122334" }
    ];

    // Find a hospital based on the city entered
    const result = mockHospitals.find(h => h.city === location.toUpperCase());
    setHospitalData(result);
  };

  // 3. UI (The Face): The HTML-like structure (JSX)
  return (
    <div className="App">
      <header className="emergency-header">
        <div className="alert-icon">üö®</div>
        <h1>RabiesAware Emergency</h1>
        <p>Immediate Response & Hospital Locator</p>
      </header>

      <main className="content">
        <div className="emergency-actions">
          <button className="btn-emergency" onClick={() => window.open('tel:112')}>
            üìû CALL EMERGENCY (112)
          </button>
          <button className="btn-firstaid">
            ü©π FIRST AID STEPS
          </button>
        </div>

        <div className="search-container">
          <h2>Find Nearby Medical Help</h2>
          <div className="search-bar">
            <input 
              type="text" 
              placeholder="Enter your City (e.g., Kochi)" 
              value={location}
              onChange={(e) => setLocation(e.target.value)}
            />
            <button className="btn-search" onClick={handleSearch}>SEARCH</button>
          </div>
        </div>

        {/* Display Result Card if a hospital is found */}
        {hospitalData ? (
          <div className="hospital-card">
            <h3>üìç {hospitalData.name}</h3>
            <p><strong>Distance:</strong> {hospitalData.distance}</p>
            <p><strong>Emergency Contact:</strong> {hospitalData.phone}</p>
            <button className="btn-call" onClick={() => window.open(`tel:${hospitalData.phone}`)}>
              CALL HOSPITAL NOW
            </button>
          </div>
        ) : (
          location && <p className="no-result">Searching for medical facilities in {location}...</p>
        )}
      </main>
    </div>
  );
}

export default App;
