import React, { useState } from 'react';
import './App.css';

function App() {
  // --- Memory (State) ---
  const [tagId, setTagId] = useState('');
  const [dogData, setDogData] = useState(null);
  const [location, setLocation] = useState('');
  const [hospitalData, setHospitalData] = useState(null);

  // --- Logic (Actions) ---
  const handleDogSearch = () => {
    const mockDogs = [
      { DogID: "RED108", TagNumber: "108", Status: "VACCINATED", Date: "01-02-2026" },
      { DogID: "YELLOW220", TagNumber: "220", Status: "BOOSTER NEEDED", Date: "08-02-2025" }
    ];
    const result = mockDogs.find(d => d.DogID === tagId.toUpperCase());
    setDogData(result);
  };

  const handleHospitalSearch = () => {
    const mockHospitals = [
      { city: "KOCHI", name: "City Medical Center", distance: "0.8 km", phone: "+91 484 2345678" }
    ];
    const result = mockHospitals.find(h => h.city === location.toUpperCase());
    setHospitalData(result);
  };

  return (
    <div className="App">
      {/* HEADER: Matches your photo */}
      <header className="emergency-header">
        <h1>RabiesAware Emergency</h1>
        <p>Immediate Response & Hospital Locator</p>
        <div className="emergency-actions">
          <button className="btn-emergency">üìû CALL EMERGENCY (112)</button>
          <button className="btn-firstaid">ü©π FIRST AID STEPS</button>
        </div>
      </header>

      <main className="content">

        {/* NEW SECTION: Tag Checking */}
        <div className="search-container tag-box">
          <h2>Verify Vaccination Tag</h2>
          <div className="search-bar">
            <input 
              type="text" 
              placeholder="Enter Tag ID (e.g. RED108)" 
              value={tagId}
              onChange={(e) => setTagId(e.target.value)}
            />
            <button className="btn-search blue" onClick={handleDogSearch}>VERIFY</button>
          </div>
          {dogData && (
            <div className="hospital-card dog-result">
              <h3>üêï Status: {dogData.Status}</h3>
              <p>Tag No: {dogData.TagNumber} | Expiry: {dogData.Date}</p>
            </div>
          )}
        </div>

        {/* EXISTING SECTION: Hospital Locator (From your photo) */}
        <div className="search-container">
          <h2>Find Nearby Medical Help</h2>
          <div className="search-bar">
            <input 
              type="text" 
              placeholder="Enter City (e.g. Kochi)" 
              value={location}
              onChange={(e) => setLocation(e.target.value)}
            />
            <button className="btn-search" onClick={handleHospitalSearch}>SEARCH</button>
          </div>
          {hospitalData && (
            <div className="hospital-card">
              <h3 className="hosp-name">üìç {hospitalData.name}</h3>
              <p><strong>Distance:</strong> {hospitalData.distance}</p>
              <p><strong>Emergency Contact:</strong> {hospitalData.phone}</p>
              <button className="btn-call">CALL HOSPITAL NOW</button>
            </div>
          )}
        </div>

      </main>
    </div>
  );
}

export default App;
